        "excerpt":" Basic   import { default as Router, Route } from 'react-router'  const routes = (   &lt;Route&gt;     &lt;Route path='*' handler={RootView} /&gt;   &lt;/Route&gt; )  Router.run(routes, Router.HashLocation, (Root) =&gt; {   React.render(&lt;Root /&gt;, document.getElementById('all')) })   Nesting   const routes = (   &lt;Route handler={Chrome}&gt;     &lt;Route path='about' handler={About} /&gt;     &lt;Route path='inbox' handler={Inbox} /&gt;     &lt;Route path='messages/:id' handler={Message} /&gt;   &lt;/Route&gt; )  import { RouteHandler } from 'react-router'  const Chrome = React.createClass({   render () {     return (       &lt;div&gt;         &lt;h1&gt;App&lt;/h1&gt;         &lt;RouteHandler /&gt;       &lt;/div&gt;     )   } })   URL params   var Message = React.createClass({   componentDidMount: function () {     // from the path `/inbox/messages/:id`     var id = this.props.params.id     ...   Link   import { Link } from 'react-router'  &lt;!-- make a named route `user` --&gt; &lt;Link to='user' params={{userId: 10}} /&gt;  &lt;Link to='login'   activeClassName='-active'   onClick='...'&gt;    Other config   &lt;Route path='/'&gt;   &lt;DefaultRoute handler={Home} /&gt;   &lt;NotFoundRoute handler={NotFound} /&gt;      &lt;Redirect from='login' to='sessions/new' /&gt;   &lt;Redirect from='login' to='sessions/new' params={{from: 'home'}} /&gt;   &lt;Redirect from='profile/:id' to='about-user' /&gt;    &lt;Route name='about-user' ... /&gt;   Router.create   var router = Router.create({   routes: &lt;Route&gt;...&lt;/Route&gt;,   location: Router.HistoryLocation })  router.run((Root) =&gt; { ... })   Navigation   import { Navigation } from 'react-router'  React.createClass({   mixins: [ Navigation ], ... })  this   .transitionTo('user', {id: 10})   .transitionTo('/path')   .transitionTo('http://...')   .replaceWith('about')   .makePath('about') // return URL   .makeHref('about') // return URL   .goBack()   ","categories": ["React"],
